"use client";
import React, { createContext, useContext, useState, useEffect } from "react";

const ThemeContext = createContext();

export function ThemeProvider({ children }) {
  const [theme, setTheme] = useState("light");
  const [hasMounted, setHasMounted] = useState(false); // prevents flicker without skipping hooks

  // ---- Load theme BEFORE showing UI ----
  useEffect(() => {
    const userPreference = localStorage.getItem("theme");

    if (userPreference) {
      setTheme(userPreference);
      document.documentElement.classList.toggle(
        "dark",
        userPreference === "dark"
      );
    } else {
      const systemPreference = window.matchMedia(
        "(prefers-color-scheme: dark)"
      ).matches;

      const initialTheme = systemPreference ? "dark" : "light";
      setTheme(initialTheme);
      document.documentElement.classList.toggle("dark", systemPreference);
    }

    setHasMounted(true); // UI can now render safely
  }, []);

  // ---- Status bar / meta theme-color ----
  useEffect(() => {
    if (!hasMounted) return;
    let meta = document.querySelector('meta[name="theme-color"]');
    if (!meta) {
      meta = document.createElement("meta");
      meta.setAttribute("name", "theme-color");
      document.head.appendChild(meta);
    }

    const color = theme === "dark" ? "#000000" : "#ffffff";
    meta.setAttribute("content", color);
  }, [theme, hasMounted]);

  const toggleTheme = () => {
    const newTheme = theme === "dark" ? "light" : "dark";
    setTheme(newTheme);
    localStorage.setItem("theme", newTheme);
    document.documentElement.classList.toggle("dark", newTheme === "dark");
  };

  // ---- Render loading screen until theme is loaded ----
  if (!hasMounted) {
    return (
      <div className="fixed inset-0 bg-white flex items-center justify-center z-50">
        <div className="flex flex-col items-center">
          {/* Replace with your actual logo */}
          <svg
          className="animate-pulse w-[150px] h-[150px] md:w-[250px] md:h-[250px]"
            width="400"
            height="400"
            viewBox="0 0 100 100"
            fill="none"
            xmlns="http://www.w3.org/2000/svg"
            xmlnsXlink="http://www.w3.org/1999/xlink"
          >
            <rect width="100" height="100" fill="url(#pattern0_960_1109)" />
            <defs>
              <pattern
                id="pattern0_960_1109"
                patternContentUnits="objectBoundingBox"
                width="1"
                height="1"
              >
                <use
                  xlinkHref="#image0_960_1109"
                  transform="translate(-0.681983 -0.610995) scale(0.00117509)"
                />
              </pattern>
              <image
                id="image0_960_1109"
                width="2000"
                height="2000"
                preserveAspectRatio="none"
                xlinkHref="data:image/png;base64,"
              />
            </defs>
          </svg>
        </div>
      </div>
    );
  }

  return (
    <ThemeContext.Provider value={{ theme, toggleTheme }}>
      {children}
    </ThemeContext.Provider>
  );
}

export function useTheme() {
  return useContext(ThemeContext);
}
